/* eslint-disable react/button-has-type */
import React from 'react';
import fetch from 'isomorphic-fetch';

class PropertyManagement extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: props.data,
      neighborhood: [],
    };
  }


  componentDidMount() {
    this.getNeighborhoodList();
  }

  getNeighborhoodList = async () => {
    const result = await fetch('http://localhost:8000/neighborhood')
      .then((res) => res.json())
      .then((list) => {
        if (list) {
          this.setState({ neighborhood: list });
        }
      })
      .catch((err) => console.log(err));

    return result;
  }

  render() {
    const {
      neighborhood,
    } = this.state;

    return (
      <form method="" action="">
        <h2>Inserir Propriedade</h2>


        <Select
          hasLabel
          htmlFor="select"
          label="Select"
          options={neighborhood}
        />

        <Select
  hasLabel
  htmlFor="select"
  label="Select"
  options={neighborhood}
/>

        <Checkbox
          hasLabel
          htmlFor="checkbox"
          label="Checkbox"
          required
        />

        <Radio
          hasLabel
          htmlFor="radioOne"
          label="Locação"
          name="radios"
          required
        />

        <Radio
          hasLabel
          htmlFor="radioTwo"
          label="Venda"
          name="radios"
          required
        />

        <Input
          hasLabel
          htmlFor="numberInput"
          label="Number input"
          required
          type="number"
          min="0"
          max="10000000000"
          step="500"
        />

        <Button
          type="submit"
          value="submit"
          text="Send form"
        />
      </form>
    );
  }
}

const Button = (props) => {
  const {
    type,
    value,
    text,
  } = props;

  return (
    <fieldset>
      <button
        type={type || 'button'}
        value={value || null}
      >
        {text}
      </button>
    </fieldset>
  );
};


const Checkbox = (props) => {
  const {
    htmlFor,
    label,
    name,
    required,
  } = props;

  return (
    <fieldset>
      <label
        htmlFor={htmlFor}
        label={label}
      >
        <input
          id={htmlFor}
          name={name || null}
          required={required || null}
          type="checkbox"
        />
        {label}
      </label>
    </fieldset>
  );
};

const Label = (props) => {
  const {
    hasLabel,
    label,
    htmlFor,
  } = props;

  if (hasLabel === 'true') {
    return <label htmlFor={htmlFor}>{label}</label>;
  }

  return null;
};

const Input = (props) => {
  const {
    htmlFor,
    label,
    name,
    required,
    hasLabel,
    max,
    min,
    placeholder,
    step,
    type,
  } = props;

  return (
    <fieldset>
      <Label
        hasLabel={hasLabel}
        htmlFor={htmlFor}
        label={label}
      />

      <input
        id={htmlFor}
        max={max || null}
        min={min || null}
        name={name || null}
        placeholder={placeholder || null}
        required={required || null}
        step={step || null}
        type={type || 'text'}
      />
    </fieldset>
  );
};

const Radio = (props) => {
  const {
    htmlFor,
    label,
    name,
    required,
  } = props;

  return (
    <fieldset>
      <label
        htmlFor={htmlFor}
        label={label}
      >
        <input
          id={htmlFor}
          name={name || null}
          required={required || null}
          type="radio"
        />
        {label}
      </label>
    </fieldset>
  );
};


const Select = (props) => {
  const {
    hasLabel,
    htmlFor,
    label,
    name,
    required,
    options,
  } = props;

  const arrayOptions = options.map((item) => item.name);

  // eslint-disable-next-line max-len
  const selectOptionsList = arrayOptions.map((option, index) => <option key={option} value={index}>{option}</option>);

  return (
    <fieldset>
      <Label
        hasLabel={hasLabel}
        htmlFor={htmlFor}
        label={label}
      />

      <select
        defaultValue=""
        id={htmlFor}
        name={name || null}
        required={required || null}
      >
        <option value="" disabled>Escolha um bairro</option>

        {selectOptionsList}
      </select>
    </fieldset>
  );
};


export default PropertyManagement;
