const Label = (props) => {
  const {
    hasLabel,
    label,
    htmlFor,
  } = props;

  if (hasLabel === true) {
    return <label htmlFor={htmlFor}>{label}</label>;
  }

  return null;
};

const Input = (props) => {
  const {
    htmlFor,
    label,
    name,
    required,
    hasLabel,
    placeholder,
    type,
    purpose,
  } = props;

  return (
    <fieldset>
      <Label
        hasLabel={hasLabel}
        htmlFor={htmlFor}
        label={label}
      />

      <input
        id={htmlFor}
        max={purpose == enums.purposeOfProperty.renting ? 50000 : 10000000 || null}
        min={purpose == enums.purposeOfProperty.renting ? 200 : 10000 || null}
        name={name || null}
        placeholder={placeholder || null}
        required={required || null}
        step={purpose == enums.purposeOfProperty.renting ? 50 : 5000 || null}
        type={type || 'text'}
      />
    </fieldset>
  );
};

const Radio = (props) => {
  const {
    htmlFor,
    label,
    name,
    required,
    setPurposeValue,
    purpose,
  } = props;

  return (
    <fieldset>
      <label
        htmlFor={htmlFor}
        label={label}
      >
        <input
          id={htmlFor}
          name={name || null}
          required={required || null}
          type="radio"
          onClick={setPurposeValue}
          value={purpose}
        />
        {label}
      </label>
    </fieldset>
  );
};


const Select = (props) => {
  const {
    hasLabel,
    htmlFor,
    label,
    name,
    required,
    options,
  } = props;

  const arrayOptions = options.map((item) => item.name || item.type);

  // eslint-disable-next-line max-len
  const selectOptionsList = arrayOptions.map((option, index) => <option key={option} value={index}>{option}</option>);

  return (
    <fieldset>
      <Label
        hasLabel={hasLabel}
        htmlFor={htmlFor}
        label={label}
      />

      <select
        defaultValue=""
        id={htmlFor}
        name={name || null}
        required={required || null}
      >
        <option value="" disabled>Escolha uma opção</option>

        {selectOptionsList}
      </select>
    </fieldset>
  );
};


export default PropertyManagement;
