  }

  render() {
    const {
      neighborhood,
      type,
      sendStatus,
    } = this.state;

    return (
      <form>
        <h2>Inserir Propriedade</h2>


        <Select
          hasLabel
          htmlFor="neighborhood-list"
          label="Bairro"
          options={neighborhood}
          getInputValue={this.getInputValue}
        />

        <Select
          hasLabel
          htmlFor="property-type"
          label="Tipo de imÃ³vel"
          options={type}
        />

        {/*
        <Checkbox
