          label="Checkbox"
          required
        /> */}

        <Radio
          hasLabel
          htmlFor="radioOne"
          label="LocaÃ§Ã£o"
          name="purpose"
          required
        />

        <Radio
          hasLabel
          htmlFor="radioTwo"
          label="Venda"
          name="purpose"
          required
        />

        <Input
          hasLabel
          htmlFor="numberInput"
          label="PreÃ§o"
          required
          type="number"
          min="0"
          max="10000000000"
          step="500"
        />

        <fieldset>
          <button
            type={type || 'button'}
            onClick={this.onSubmit}
          >
            Cadastrar
