/* eslint-disable react/button-has-type */
import React from 'react';
import fetch from 'isomorphic-fetch';

class PropertyManagement extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: props.data,
      neighborhood: [],
    };
  }


  componentDidMount() {
    this.getNeighborhoodList();
  }

  getNeighborhoodList = async () => {
    const result = await fetch('http://localhost:8000/neighborhood')
      .then((res) => res.json())
      .then((list) => {
        if (list) {
          this.setState({ neighborhood: list });
        }
      })
      .catch((err) => console.log(err));

    return result;
  }

  render() {
    const {
      neighborhood,
    } = this.state;

    return (
      <form method="" action="">
        <h2>Inserir Propriedade</h2>
        <Input
          hasLabel
          htmlFor="textInput"
          label="Text input"
          required
          type="text"
        />

        <Input
          hasLabel
          htmlFor="emailInput"
          label="Email input"
          required
          type="email"
        />

        <Input
          hasLabel
          htmlFor="numberInput"
          label="Number input"
          required
          type="number"
          min="0.5"
          max="100"
          step="0.5"
        />

        <Input
          hasLabel
          htmlFor="passwordInput"
          label="Password input"
          required
          type="password"
        />
        <Select
          hasLabel
          keyid="teste"

          htmlFor="select"
          label="Select"
          options={neighborhood}
        />

        <Datalist
          hasLabel
          htmlFor="datalist"
          label="Datalist"
          options="Chrome, Edge, Firefox, Internet Explorer, Opera, Safari, Vivaldi"
          required
          keyid="id"
        />

        <Textarea
          hasLabel
          htmlFor="textarea"
          label="Textarea"
          required
        />

        <Checkbox
          hasLabel
          htmlFor="checkbox"
          label="Checkbox"
          required
        />

        <Radio
          hasLabel
          htmlFor="radioOne"
          label="Radio one"
          name="radios"
          required
        />

        <Radio
          hasLabel
          htmlFor="radioTwo"
          label="Radio two"
          name="radios"
          required
        />

        <Button
          type="submit"
          value="submit"
          text="Send form"
        />
      </form>
    );
  }
}

const Button = (props) => {
  const {
    type,
    value,
    text,
  } = props;

  return (
    <fieldset>
      <button
        type={type || 'button'}
        value={value || null}
      >
        {text}
      </button>
    </fieldset>
  );
};

const Datalist = (props) => {
  const {
    options,
    hasLabel,
    htmlFor,
    label,
    name,
    required,
    keyid,
  } = props;

  const dataOptions = options.split(', ');

  // eslint-disable-next-line jsx-a11y/control-has-associated-label
  const dataOptionsList = dataOptions.map((dataOption, index) => <option key={`${keyid}${index}`} value={dataOption} />);

  return (
    <fieldset>
      <Label
        hasLabel={hasLabel}
        htmlFor={htmlFor}
        label={label}
      />

      <input list={htmlFor} />

      <datalist
        defaultValue=""
        id={htmlFor}
        name={name || null}
        required={required || null}
      >
        <option value="" disabled>Select one option</option>

        {dataOptionsList}
      </datalist>
    </fieldset>
  );
};

const Checkbox = (props) => {
  const {
    htmlFor,
    label,
    name,
    required,
  } = props;

  return (
    <fieldset>
      <label
        htmlFor={htmlFor}
        label={label}
      >
        <input
          id={htmlFor}
          name={name || null}
          required={required || null}
          type="checkbox"
        />
        {label}
      </label>
    </fieldset>
  );
};

const Label = (props) => {
  const {
    hasLabel,
    label,
    htmlFor,
  } = props;

  if (hasLabel === 'true') {
    return <label htmlFor={htmlFor}>{label}</label>;
  }

  return null;
};

const Input = (props) => {
  const {
    htmlFor,
    label,
    name,
    required,
    hasLabel,
    max,
    min,
    placeholder,
    step,
    type,
  } = props;

  return (
    <fieldset>
      <Label
        hasLabel={hasLabel}
        htmlFor={htmlFor}
        label={label}
      />

      <input
        id={htmlFor}
        max={max || null}
        min={min || null}
        name={name || null}
        placeholder={placeholder || null}
        required={required || null}
        step={step || null}
        type={type || 'text'}
      />
    </fieldset>
  );
};

const Radio = (props) => {
  const {
    htmlFor,
    label,
    name,
    required,
  } = props;

  return (
    <fieldset>
      <label
        htmlFor={htmlFor}
        label={label}
      >
        <input
          id={htmlFor}
          name={name || null}
          required={required || null}
          type="radio"
        />
        {label}
      </label>
    </fieldset>
  );
};


const Select = (props) => {
  const {
    hasLabel,
    htmlFor,
    label,
    name,
    required,
    keyid,
    options,
  } = props;

  const arrayOptions = options.map((item) => item.name);
  const selectOptions = arrayOptions.split(', ');

  // eslint-disable-next-line max-len
  //   const selectOptionsList = selectOptions.map((selectOption, index) => <option key={`${keyid}-${index}`} value={index}>{selectOption}</option>);

  return (
    <fieldset>
      <Label
        hasLabel={hasLabel}
        htmlFor={htmlFor}
        label={label}
      />

      <select
        defaultValue=""
        id={htmlFor}
        name={name || null}
        required={required || null}
      >
        <option value="" disabled>Select one option</option>

        {/* {selectOptionsList} */}
      </select>
    </fieldset>
  );
};

const Textarea = (props) => {
  const {
    hasLabel,
    htmlFor,
    label,
    cols,
    name,
    required,
    rows,
  } = props;

  return (
    <fieldset>
      <Label
        hasLabel={hasLabel}
        htmlFor={htmlFor}
        label={label}
      />

      <textarea
        cols={cols || null}
        id={htmlFor}
        name={name || null}
        required={required || null}
        rows={rows || null}
      />
    </fieldset>
  );
};


export default PropertyManagement;
