/* eslint-disable react/button-has-type */
import React from 'react';
import fetch from 'isomorphic-fetch';

import enums from '../../enums';

class PropertyManagement extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: props.data,
      neighborhood: [],
      type: [],
      messages: '',
      purpose: '',
    };
  }


  componentDidMount() {
    this.getNeighborhoodList();
    this.getTypeList();
  }

  setPurposeValue = (e) => {
    console.log(e.target);
  }

  getNeighborhoodList = async () => {
    const result = await fetch('http://localhost:8000/neighborhood')
      .then((res) => res.json())
      .then((list) => {
        if (list) {
          this.setState({ neighborhood: list });
        }
      })
      .catch(() => this.setState({ message: 'Erro ao encontrar lista de bairros' }));

    return result;
  }

  getTypeList = async () => {
    const result = await fetch('http://localhost:8000/typeproperty')
      .then((res) => res.json())
      .then((list) => {
        if (list) {
          this.setState({ type: list });
        }
      })
      .catch(() => this.setState({ message: 'Erro ao encontrar lista de tipos' }));

    return result;
  }

  render() {
    const {
      neighborhood,
      messages,
      type,
    } = this.state;

    return (
      <form method="" action="">
        <h2>Inserir Propriedade</h2>


        <Select
          hasLabel
          htmlFor="select"
          label="Bairro"
          options={neighborhood}
        />

        <Select
          hasLabel
          htmlFor="select"
          label="Tipo de imóvel"
          options={type}
        />
        {/*
        <Checkbox
          hasLabel
          htmlFor="checkbox"
          label="Checkbox"
          required
        /> */}

        <Radio
          hasLabel
          setPurposeValue={this.setPurposeValue}
          htmlFor="radioOne"
          label="Locação"
          name="radios"
          purpose={enums.purposeOfProperty.renting}
          required
        />

        <Radio
          hasLabel
          htmlFor="radioTwo"
          label="Venda"
          purpose={enums.purposeOfProperty.selling}
          name="radios"
          required
        />

        <Input
          hasLabel
          htmlFor="numberInput"
          label="Preço"
          required
          type="number"
          min="0"
          max="10000000000"
          step="500"
        />

        <Button
          type="submit"
          value="submit"
          text="Send form"
        />

      </form>
    );
  }
}

const Button = (props) => {
  const {
    type,
    value,
    text,
  } = props;

  return (
    <fieldset>
      <button
        type={type || 'button'}
        value={value || null}
      >
        {text}
      </button>
    </fieldset>
  );
};


