/* eslint-disable react/button-has-type */
import React from 'react';
import fetch from 'isomorphic-fetch';

import enums from '../../enums';

class PropertyManagement extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: props.data,
      neighborhood: [],
      type: [],
      messages: '',
    };
  }


  componentDidMount() {
    this.getNeighborhoodList();
    this.getTypeList();
  }

  getNeighborhoodList = async () => {
    const result = await fetch('http://localhost:8000/neighborhood')
      .then((res) => res.json())
      .then((list) => {
        if (list) {
          this.setState({ neighborhood: list });
        }
      })
      .catch(() => this.setState({ message: 'Erro ao encontrar lista de bairros' }));

    return result;
  }

  getTypeList = async () => {
    const result = await fetch('http://localhost:8000/typeproperty')
      .then((res) => res.json())
      .then((list) => {
        if (list) {
          this.setState({ type: list });
        }
      })
      .catch(() => this.setState({ message: 'Erro ao encontrar lista de tipos' }));

    return result;
  }

  handleSubmit = () => {

  }

  render() {
    const {
      neighborhood,
      messages,
      type,
    } = this.state;

    return (
      <form method="" action="">
        <h2>Inserir Propriedade</h2>


        <Select
          hasLabel
          htmlFor="select"
          label="Bairro"
          options={neighborhood}
        />

        <Select
          hasLabel
          htmlFor="select"
          label="Tipo de imóvel"
          options={type}
        />

        {/*
        <Checkbox
          hasLabel
          htmlFor="checkbox"
          label="Checkbox"
          required
        /> */}

        <Radio
          hasLabel
          htmlFor="radioOne"
          label="Locação"
          name="radios"
          required
        />

        <Radio
          hasLabel
          htmlFor="radioTwo"
          label="Venda"
          name="radios"
          required
        />

        <Input
          hasLabel
          htmlFor="numberInput"
          label="Preço"
          required
          type="number"
          min="0"
          max="10000000000"
          step="500"
        />

=
        <fieldset>
          <button
           type={type || 'button'}
           value={value || null}
         >
           {text}
         </button>
        </fieldset>


      </form>
    );
  }
}


