/* eslint-disable react/button-has-type */
import React from 'react';
import fetch from 'isomorphic-fetch';

import enums from '../../enums';

class PropertyManagement extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      neighborhood: [],
      type: [],
      purpose: '',
      sendStatus: '',
    };
  }

  componentDidMount() {
    this.getNeighborhoodList();
    this.getTypeList();
  }

  getNeighborhoodList = async () => {
    const result = await fetch('http://localhost:8000/neighborhood')
      .then((res) => res.json())
      .then((list) => {
        if (list) {
          this.setState({ neighborhood: list });
        }
      })
      .catch(() => this.setState({ sendStatus: 'Erro ao encontrar lista de bairros' }));

    return result;
  }

  getTypeList = async () => {
    const result = await fetch('http://localhost:8000/typeproperty')
      .then((res) => res.json())
      .then((list) => {
        if (list) {
          this.setState({ type: list });
        }
      })
      .catch(() => this.setState({ sendStatus: 'Erro ao encontrar lista de tipos' }));

    return result;
  }


  onSubmit = () => {
    const selectBlock = document.getElementById('neighborhood-list').value;
    const selectType = document.getElementById('property-type').value;
    const purpose = document.querySelector('input[name="purpose"]:checked').value;
    const price = document.getElementById('price').value;

    // if (neighborhood === 0 || contactName.length === 0) {
    //   this.setState({
    //     sendStatus: 'Ops, algo de errado aconteceu, recarregue a página!!',
    //   });
    // } else {
    //   this.setState({ sendStatus: 'Enviando ...' });
    //   fetch('https://dry-island-26655.herokuapp.com/contactLMS', {
    //     method: 'post',
    //     headers: { 'Content-Type': 'application/json' },
    //     body: JSON.stringify({
    //       email: contactEmail,
    //       message: contactMessage,
    //       name: contactName,
    //       phone: contactPhone,
    //     }),
    //   })
    //     .then((response) => response.json())
    //     .then((message) => {
    //       if (message) {
    //         this.setState({
    //           sendStatus: 'Enviada com sucesso',
    //         });
    //       }
    //     })
    //     .catch((err) => {
    //       this.setState({
    //         sendStatus: 'Algo deu errado, tente novamente mais tarde',
    //       });
    //     });
    // }
  }


  render() {
    const {
      neighborhood,
      type,
      sendStatus,
    } = this.state;

    return (
      <form>
        <h2>Inserir Propriedade</h2>


        <Select
          hasLabel
          htmlFor="neighborhood-list"
          label="Bairro"
          options={neighborhood}
          getInputValue={this.getInputValue}
        />

        <Select
          hasLabel
          htmlFor="property-type"
          label="Tipo de imóvel"
          options={type}
        />

        {/*
        <Checkbox
          hasLabel
          htmlFor="checkbox"
          label="Checkbox"
          required
        /> */}

        <Radio
          hasLabel
          htmlFor="radioOne"
          label="Locação"
          name="purpose"
          value={enums.purposeOfProperty.renting}
          required
        />

        <Radio
          hasLabel
          htmlFor="radioTwo"
          label="Venda"
          value={enums.purposeOfProperty.selling}
          name="purpose"
          required
        />

        <Input
          hasLabel
          htmlFor="price"
          label="Preço"
          required
          type="number"
          min="0"
          max="10000000000"
          step="500"
        />

        <fieldset>
          <button
            type={type || 'button'}
            onClick={this.onSubmit}
          >
            Cadastrar
          </button>
        </fieldset>
        {sendStatus}

      </form>
    );
  }
}


